#include <cstdio>
#include <vector>
#include <algorithm>

using namespace std;

class Solution {
private:

    struct Node {
        int value;
        int idx;
        Node(int _value, int _idx) : value(_value), idx(_idx) {}
    };

public:
    
    static int cmp(const Node &a, const Node &b) {
        return a.value < b.value;
    }

    static int cmp_tuple(const vector<int> &a, const vector<int> &b) {
        return (a[0] < b[0] || a[0] == b[0] && a[1] < b[1] || a[0] == b[0] && a[1] == b[1] && a[2] < b[2]);
    }

    static bool equal_node(const Node &a, const Node &b) {
        return a.value == b.value;
    }

    bool exists(const vector<int> &a, const vector<vector<int> > &ret) {
        for (int i = 0; i < ret.size(); i++) {
            if (a[0] == ret[i][0] && a[1] == ret[i][1] && a[2] == ret[i][2]) return true;
        }
        return false;
    }

    vector<vector<int> > threeSum(vector<int>& nums) {
        vector<Node> tmp1;
        for (int i = 0; i < nums.size(); i++) {
            tmp1.push_back(Node(nums[i], i));
        }
        sort(tmp1.begin(), tmp1.end(), cmp);
        vector<Node> tmp;
        int len = nums.size();
        
        for (int i = 0; i < len; i ++) {
            if (tmp.size() < 3 || !equal_node(tmp1[i], tmp[tmp.size() - 1]) || !equal_node(tmp1[i], tmp[tmp.size() - 2]) || !equal_node(tmp1[i], tmp[tmp.size() - 3])) 
                tmp.push_back(tmp1[i]);
        }
        len = tmp.size();
        vector<vector<int> > ret;
        //vector<int> last_tuple;
        for (int i = 0; i < len; i++) {
            int ed = len - 1;
            for (int j = i + 1; j < len; j++) {
                while (ed > j && tmp[ed].value + tmp[i].value + tmp[j].value > 0) ed --;
                if (ed <= j) break;
                if (tmp[ed].value + tmp[i].value + tmp[j].value == 0) {
                    vector<int> tuple;
                    tuple.push_back(tmp[i].value);
                    tuple.push_back(tmp[j].value);
                    tuple.push_back(tmp[ed].value);
                    if (exists(tuple, ret) == false) {
                        ret.push_back(tuple);
                    //    last_tuple = tuple;
                    }
                }
            }
        }
       // sort(ret.begin(), ret.end(), cmp_tuple);
       // int ret_len = unique(ret.begin(), ret.end()) - ret.begin();
       // ret.resize(ret_len);
        return ret;
    }
};

